import ReactDOM from "react-dom";
import SortingVisualizer from "./SortingVisualizer/SortingVisualizer.jsx";

// ## Rendering SortingVisualizer Component ( Imported from path mentioned above ) ## //

import React, { useState } from "react";
import { InfoSideOver } from "./components/InfoSideOver";
import { SearchingAlgoEnum } from "./interfaces";
import { classNames } from "./utils/helpers";

function App() {
  const [selectedAlgorithm, setSelectedAlgorithm] = useState<SearchingAlgoEnum | null>(null);

  const handleAlgorithmClick = (algorithm: SearchingAlgoEnum) => {
    setSelectedAlgorithm(algorithm);
  };

  const handleClose = () => {
    setSelectedAlgorithm(null);
  };

  return (
    <div className="flex flex-col h-screen">
      <header className="bg-gray-800 pb-2">
        <nav className="px-4 py-2 flex items-center justify-between sm:px-6">
          <div className="flex items-center">
            <h1 className="text-2xl font-semibold text-white">Algorithms Visualizer</h1>
          </div>
        </nav>
      </header>
      <main className="flex-grow">
        <div className="flex items-center justify-center h-full">
          <div className="grid grid-cols-3 gap-4">
            <button
              className={classNames(
                "p-6 bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow",
                selectedAlgorithm === SearchingAlgoEnum.DIJKSTRA && "ring-2 ring-blue-500"
              )}
              onClick={() => handleAlgorithmClick(SearchingAlgoEnum.DIJKSTRA)}
            >
              <h2 className="text-lg font-medium text-gray-900">Dijkstra's algorithm</h2>
              <p className="mt-4 text-gray-500">
                Find the shortest path between two nodes in a graph.
              </p>
            </button>
            <button
              className={classNames(
                "p-6 bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow",
                selectedAlgorithm === SearchingAlgoEnum.BFS && "ring-2 ring-blue-500"
              )}
              onClick={() => handleAlgorithmClick(SearchingAlgoEnum.BFS)}
            >
              <h2 className="text-lg font-medium text-gray-900">Breadth-first search</h2>
              <p className="mt-4 text-gray-500">Explore a tree or graph in a breadth-first order.</p>
            </button>
            <button
              className={classNames(
                "p-6 bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow",
                selectedAlgorithm === SearchingAlgoEnum.DFS && "ring-2 ring-blue-500"
              )}
              onClick={() => handleAlgorithmClick(SearchingAlgoEnum.DFS)}
            >
              <h2 className="text-lg font-medium text-gray-900">Depth-first search</h2>
              <p className="mt-4 text-gray-500">
                Explore a tree or graph in a depth-first order.
              </p>
            </button>
          </div>
        </div>
      </main>
      <InfoSideOver algorithm={selectedAlgorithm} onClose={handleClose} />
    </div>
  );
}

export default App;

ReactDOM.render(<SortingVisualizer />, document.getElementById("root"));
